import React, { useState } from 'react';
import { IoMdCloseCircleOutline } from 'react-icons/io';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCookies } from 'react-cookie';

const AuthModel = ({ setShowModel, isSignUp }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [cookies, setCookie] = useCookies(['AuthToken', 'UserId']);
  const navigate = useNavigate();

  const handleClick = () => {
    setShowModel(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isSignUp && password !== confirmPassword) {
        console.log('Passwords do not match!');
        return;
      }

      const response = await axios.post(`http://localhost:8000/${isSignUp ? 'signup' : 'login'}`, {
        email,
        password,
      });

      if (response.status === 201) {
        setCookie('AuthToken', response.data.token, { path: '/' });
        setCookie('UserId', response.data.userId, { path: '/' });

        if (isSignUp) {
          navigate('/onboarding');
        } else {
          navigate('/dashboard');
        }
      }
    } catch (error) {
      console.error('Error during authentication:', error);
      // Handle error states or display error messages to the user
    }
  };

  return (
    <div className="auth-modal">
      <div className="close-icon" onClick={handleClick}>
        <IoMdCloseCircleOutline />
      </div>
      <h2>{isSignUp ? 'CREATE ACCOUNT' : 'Sign In'}</h2>
      <p>By clicking Log In, you agree to our terms. Learn how we process your data in our Privacy Policy and Cookie Policy.</p>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        {isSignUp && (
          <input
            type="password"
            name="confirmPassword"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        )}
        <input type="submit" className="secondary-button" value={isSignUp ? 'Sign Up' : 'Log In'} />
      </form>
      <hr />
      <h2>GET THE APP</h2>
    </div>
  );
};

export default AuthModel;
